import React, { useState, useEffect } from 'react'
import Modal from 'react-modal'
import {Document, Page, pdfjs} from 'react-pdf'

import 'react-pdf/dist/esm/Page/AnnotationLayer.css';

import { ArrowLeftIcon, ArrowRightIcon } from '@primer/octicons-react';

Modal.setAppElement('#root')

export default function ModalElement(props) {

    const socket = props.socket
    const exam = props.exam

    const [show, setShow] = useState(false)
    const [doc, setDoc] = useState(null)
    const [pageNum, setPageNum] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const [newDoc, setNewDoc] = useState(null)

    const open = () => setShow(true)
    const close = () => setShow(false)

    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`

    useEffect(() => {
        socket.emit('examDoc-signal', exam)

        socket.on('examDoc-emitter', document => {
            if(document){
                setDoc(document)
            }
        })
        // eslint-disable-next-line
    }, [show, forceUpdate])

    function onDocumentLoadSuccess(numPages) {
        setPageNum(numPages)
    }

    function nextPage(event){
        event.preventDefault()
        if(pageNum){
            setCurrentPage(page => {
                if(page+1 > pageNum){
                    return page
                }else{
                    return page+1
                }
            })
        }
    }
    function prevPage(event){
        event.preventDefault()
        if(pageNum){
            setCurrentPage(page => {
                if(page-1 < 1){
                    return page
                }else{
                    return page-1
                }
            })
        }
    }

    function handleChange(event){
        setNewDoc(event.target.files[0])
    }

    function handleSubmit(event){
        event.preventDefault()
        if(newDoc){
            socket.emit('examDoc-modify', newDoc)
            socket.on('examDoc-response', response => {
                if(response){
                    socket.emit('examDoc-signal')
                }
            })
        }
    }

    return (
        <div className="container">
            <button className="btn btn-warning m-3" onClick={open}>
                PDF mÃ³dosÃ­tÃ¡s
            </button>

            <Modal isOpen={show} onRequestClose={close}
            style={
                {
                    overlay: {
                        marginTop: "30px",
                        backgroundColor: "rgba(0,0,0,0.3)",
                        backdropFilter: "blur(6px)"
                    },
                    content: {
                        textAlign: "center"
                    }
                }
            }>
                <div>
                    <h1>A vizsgÃ¡hoz tartozÃ³ tananyag</h1>
                    <hr className="w-75"/>
                    <div className="container">
                        <Document
                            width={300}
                            height={300}
                            loading='A virtuÃ¡lis dokumentum betÃ¶ltÃ©se...'
                            file={{data: doc}} 
                            onLoadSuccess={page => onDocumentLoadSuccess(page.numPages)}>
                                <Page pageNumber={currentPage} />
                        </Document>
                        <p>{currentPage} / {pageNum}</p>

                        <button className="btn btn-outline-primary m-2" onClick={prevPage}>
                            <i>
                                <ArrowLeftIcon/>
                            </i>
                        </button>

                        <button className="btn btn-outline-primary m-2" onClick={nextPage}>
                            <i>
                                <ArrowRightIcon/>
                            </i>
                        </button>
                    
                        <form>
                            <div className="container">
                                <input type="file" onChange={handleChange} name="examDoc"/>
                            </div>
                            <div className="container text-center">
                                <button type="submit" className="btn btn-warning mt-3" value="LÃ©trehozÃ¡s">FeltÃ¶ltÃ©s!</button>
                            </div>
                        </form>
                    </div>
                    <hr className="w-75"/>
                    <button className="btn btn-warning m-2" onClick={close}>BezÃ¡r!</button>
                </div>
            </Modal>
        </div>
    )
}
