import React, {useEffect, useState, useRef} from 'react'

export default function ListManager(props){

    const [list, setList] = useState([])
    const displayList = useRef([])

    useEffect(() =>{
        const temp = []
        setList(props.list)

        if(list.length >= 4){
            temp.push('Azonosító: '+list[0], list[1], 'Pontszám: '+list[2], list[3])
        }else{
            temp.push('Azonosító: '+list[0], list[1], 'Pontszám: '+list[2])
        }

        displayList.current = temp
    })

    return (
        <ul>
            {
                displayList.current.length === 0 ? <li>Nincs megjeleníthető tartalom</li> :
                displayList.current.map((el, index) => {
                    if(Array.isArray(el)){
                        if(index === 3){
                            return <div key={index}>
                                <p>A kérdéshez tartozó válaszok:</p>
                                <ListManager list={el} />
                            </div>
                        }
                        return <ListManager key={index} list={el} />
                    }else{
                        return <li key={index}>{el}</li>
                    }
                })
            }
        </ul>
    )
}
