import React, {useState, useEffect} from 'react'

export default function AddQuestion(props){

    const [renderedQuestions, setRenderedQuestions] = useState([])
    const [question, setQuestion] = useState(null)
    const [points, setPoints] = useState(null)

    useEffect(() => {
        console.log(props.count)
        let result = []
        for(let i = 0; i < props.count; i++){
            result.push(
                <form>
                    <input type='text' name='question' value={question || ''} placeholder='A kérdés szövege'/>
                    <input type='number' name='points' value={points || ''} />
                    <input type='submit' value='Feltöltés' />
                </form>
            )
        }
        setRenderedQuestions(result)
    })

    return (
        <div>
           { renderedQuestions.length === 0 ? null : renderedQuestions.map(value => {
               return value
           })} 
        </div>
    )
}
