import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'

import API from '../../BackendAPI'

export default function ExamModify(){

    const examCode = useParams()

    const [warning, setWarning] = useState(null)
    const [examName, setExamName] = useState(null)
    const [questions, setQuestions] = useState(null)
    const [answers, setAnswers] = useState(null)

    useEffect(() => {
        API.get(`/exams/${examCode.examName}`)
            .then(result => {
                if(result){
                    setExamName(result.data.name)
                    let questionList = []
                    result.data.questions.forEach(question => {
                        let answers = []
                        question.answers.forEach(answer => {
                            answers.push([answer.id, answer.text, answer.correct])
                        })
                        questionList.push([question.id, question.exam_id, question.name,
                            question.points, question.pic, answers])
                        setQuestions(questionList)
                        console.log(questions)
                    })
                }
            }).catch(err => console.log(err))
    }, [])

    return (
        <div>
            <h1>{examName}</h1>
            
            {/*questions.map((question) => {
            return <div>
                <h3>{question.name}</h3>
                <ul>
                    <li>Azonosító: {question.id}</li>
                    <li>Pontszám: {question.points}</li>
                    <li>A kérdéshez tartozó válaszok</li>
                </ul>
            </div>
            })*/}
        </div>
    )
}
