import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'

import {Document, Page, pdfjs} from 'react-pdf'

import API from '../../BackendAPI'

export default function ExamDocument(){

    const [examDoc, setExamDoc] = useState('/')
    const [exam,] = useState(useParams())
    const [pageNum, setPageNum] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);

    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`

    function onDocumentLoadSuccess({ pageCount }) {
        setPageNum(pageCount)
    }

    useEffect(() => {
        API.get(`/exams/learn/${exam.examCode}`)
        .then(result => {
            console.log(result.data.document)
            var len = result.data.document.length;
            var bytes = new Uint8Array( len );
            for (var i = 0; i < len; i++){
                bytes[i] = result.data.document.charCodeAt(i);
            }

            setExamDoc(bytes.buffer)
        }).catch(err => {
            console.log(err)
        })
    },[pageNum])

    return (
        <div>
          <Document
            file={examDoc}
            onLoadSuccess={onDocumentLoadSuccess}
          >
            <Page pageNumber={currentPage} />
          </Document>
          <p>Page {currentPage} of {pageNum}</p>
        </div>
      );
}
