import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'

import {Document, Page, pdfjs} from 'react-pdf'

import {io} from 'socket.io-client'

import 'react-pdf/dist/esm/Page/AnnotationLayer.css';

import { ArrowLeftIcon, ArrowRightIcon } from '@primer/octicons-react';
import { NavLink } from 'react-router-dom';

export default function ExamDocument(props){

    const exam = useParams()
    
    const [examDoc, setExamDoc] = useState('/')
    const [pageNum, setPageNum] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const [learnt, setLearnt] = useState(true)

    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`

    function onDocumentLoadSuccess(numPages) {
        setPageNum(numPages)
    }

    function nextPage(event){
        event.preventDefault()
        if(pageNum){
            setCurrentPage(page => {
                if(page+1 > pageNum){
                    return page
                }else{
                    return page+1
                }
            })
        }
    }
    function prevPage(event){
        event.preventDefault()
        if(pageNum){
            setCurrentPage(page => {
                if(page-1 < 1){
                    return page
                }else{
                    return page-1
                }
            })
        }
    }

    useEffect(() => {
        const socket = io('http://localhost:5000', {withCredentials:true})

        socket.emit('examDoc-signal', exam.examCode)

        socket.on('examDoc-emitter', document => {
            if(document){
                setExamDoc(document)
            }
        })

        return () => socket.disconnect()
    },[exam.examCode])

    useEffect(() => {
        if(currentPage === pageNum){
            setLearnt(false)
        }
    }, [currentPage, pageNum])

    return (
        <div className="container text-center bg-light rounded shadow mb-3">
            <Document
             loading='A virtuális dokumentum betöltése...'
             file={{data: examDoc.data}} 
             onLoadSuccess={page => onDocumentLoadSuccess(page.numPages)}>
                <Page pageNumber={currentPage} />
            </Document>

            <p>{currentPage} / {pageNum}</p>

            <button className="btn btn-outline-primary m-2" onClick={prevPage}>
                <i>
                    <ArrowLeftIcon/>
                </i>
            </button>

            <NavLink to="/profile">
                <button className="btn btn-outline-primary m-2">
                    Vissza!
                </button>
            </NavLink>

            <NavLink to={`/exams/${exam.examCode}`}>
                <button disabled={
                    props.permission === 'admin' ? true : learnt
                } className="btn btn-outline-primary m-2">
                    Levizsgázom!
                </button>
            </NavLink>

            <button className="btn btn-outline-primary m-2" onClick={nextPage}>
                <i>
                    <ArrowRightIcon/>
                </i>
            </button>
        </div>
      );
}
