import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'

import API from '../../BackendAPI'

export default function ExamModify(){

    const examCode = useParams()

    const [warning, setWarning] = useState(null)
    const [examName, setExamName] = useState(null)
    const [questions, setQuestions] = useState([])

    function fetchData(){
        API.get(`/exams/${examCode.examName}`)
            .then(result => {
                if(result){
                    setExamName(result.data.name)
                    setQuestions(result.data.questions)
                }
            }).catch(err => console.log(err))
    }

    function renderQuestions(){
        return questions.map((question) => {
            return <div>
                <h3>{question.name}</h3>
                <ul>
                    <li key={Math.random()}>Azonosító: {question.id}</li>
                    <li key={Math.random()}>Pontszám: {question.points}</li>
                    <li key={Math.random()}>A kérdéshez tartozó válaszok</li>
                </ul>
            </div>
        })
    }

    useEffect(() => {
        fetchData()
    })

    return (
        <div>
            <h1>{examName}</h1>
            
            {renderQuestions()}
        </div>
    )
}
