import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'

import API from '../../BackendAPI'

export default function AddAnswer(props){

    const [param,] = useState(useParams())
    const [renderedAnswers, setRenderedAnswers] = useState([])
    const [answer, setAnswer] = useState(null)
    const [isCorrect, setCorrect] = useState(null)
    const [updated, setUpdated] = useState(null)

    function handleChange(event){
        switch(event.target.name){
            case 'answer':
                setAnswer(event.target.value)
                break
            case 'value':
                if(event.target.value === 'Válasszon értéket!'){
                    break
                }
                setCorrect(event.target.value)
                break
            default:
                return
        }
    }

    function handleSubmit(event){
        event.preventDefault()
        if(props.questionId && answer && isCorrect != null){
            API.post(`/exams/modify/${param.examName}`, 
                {questionId: props.questionId, answerText: answer, value: isCorrect})
            .then(result => {
                let temp = renderedAnswers
                setUpdated(result)
                setRenderedAnswers(temp.slice(0,1))
            })
        }
    }

    useEffect(() => {
        let result = []
        for(let i = 0; i < props.count; i++){
            result.push(
                <form key={i} onSubmit={handleSubmit}>
                    <input type='text' name='answer' 
                        value={answer || ''} placeholder='A kérdés szövege' onChange={handleChange}/>
                    <select name='value' className="rounded pl-2 w-25" onChange={handleChange}>
                        <option defaultValue={null}>Válasszon értéket!</option>
                        <option value={true}>Helyes</option>
                        <option value={false}>Helytelen</option>
                    </select>
                    <input type='submit' value='Feltöltés' />
                </form>
            )
        }
        setRenderedAnswers(result)
    }, [updated, props.count, answer])

    return (
        <div>
           { renderedAnswers.length === 0 ? null : renderedAnswers.map(value => {
               return value
           })} 
        </div>
    )
}
